// BookList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Book from '../../components/Book';
import '../../css/Book.css';

const BookList = () => {
    const [books, setBooks] = useState([]);

    useEffect(() => {
        const fetchBooksAndPhotos = async () => {
            try {
                // 병렬로 Axios 요청 수행
                const [booksResponse, photosResponse] = await Promise.all([
                    axios.get('http://localhost:8080/api/bookList'),
                    axios.get('http://localhost:8080/api/photo')
                ]);

                const updatedBooks = booksResponse.data.map((book, index) => ({
                    ...book,
                    thumbnailPhoto: photosResponse.data[index].thumbnailPhoto,
                    detailPhoto: photosResponse.data[index].detailPhoto
                }));
                console.log(updatedBooks);
                setBooks(updatedBooks);
            } catch (err) {
                console.log("오류 발생!");
                console.log(err);
            }
        };

        fetchBooksAndPhotos();
    }, []);
    return (
        <div className="book-list">
            <h1>책 목록 페이지</h1>
            {books.map((book) => <Book key={book.id} book={book} />)}
        </div>
    );
}

export default BookList;
