import React from 'react';
import { Link } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import { Container, Row, Col } from 'react-bootstrap';

const BookDetail = (props) => {
    // URL에서 책의 ID를 가져옵니다.
    const { id } = useParams();

    // 여기서 책의 상세 정보를 가져오는 API 호출 등을 구현할 수 있습니다.
    // 예를 들어, axios 또는 fetch를 사용하여 서버에서 책의 상세 정보를 가져올 수 있습니다.

    // 가상의 책 상세 정보
    const bookDetail = {
        id: id,
        title: "책 제목",
        author: "저자",
        publisher: "출판사",
        created_at: "출판일",
        price: "가격",
        // 다른 필드들도 필요한 경우 여기에 추가합니다.
    };

    return (
        <Container>
            <Row className="justify-content-center">
                <Col md={8}>
                    <div className="book-detail-container">
                        <div className="book-detail">
                            <h1>책 상세 정보</h1>
                            <p>책 번호: {bookDetail.id}</p>
                            <p>책 제목: {bookDetail.title}</p>
                            <p>저자: {bookDetail.author}</p>
                            <p>출판사: {bookDetail.publisher}</p>
                            <p>출판일: {bookDetail.created_at}</p>
                            <p>가격: {bookDetail.price} 원</p>
                            {/* 세부 사진을 보여주거나 해당 기능에 맞게 수정하세요. */}
                            {/* <img className="book-detail-photo" src={bookDetail.detailPhoto} alt="Detail" /> */}
                        </div>
                        <Link to="/bookList" className="back-link">이전으로</Link>
                    </div>
                </Col>
            </Row>
        </Container>
    );
}

export default BookDetail;
