import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';

const BookDetail = () => {
    const { id } = useParams();
    const [bookDetail, setBookDetail] = useState(null);

    useEffect(() => {
        axios.get(`http://localhost:8080/api/bookDetail/${id}`)
            .then(res => {
                const bookData = res.data;
                const bookWithImageUrl = {
                    ...bookData,
                    detailPhoto: `http://localhost:8080/api/image?imageName=${bookData.detailPhoto}`
                };
                setBookDetail(bookWithImageUrl);
            })
            .catch(err => {
                console.log("오류 발생!");
                console.log(err);
            });
    }, [id]);

    if (!bookDetail) {
        return <div>로딩 중...</div>;
    }

    return (
        <div className="book-detail-container">
            <div className="book-detail">
                <h1>책 상세 정보</h1>
                <p>책 번호: {bookDetail.id}</p>
                <p>책 제목: {bookDetail.title}</p>
                <p>저자: {bookDetail.author}</p>
                <p>출판사: {bookDetail.publisher}</p>
                <p>출판일: {bookDetail.created_at}</p>
                <p>가격: {bookDetail.price} 원</p>
                {/* 세부 사진을 보여줍니다 */}
                <img className="book-detail-photo" src={bookDetail.detailPhoto} alt="Detail" />
            </div>
            <Link to="/bookList" className="back-link">이전으로</Link>
        </div>
    );
}

export default BookDetail;
