import React from 'react'
import { Card } from 'react-bootstrap'
import { Link } from 'react-router-dom';

const Book = (props) => {

    const { id, title, author, publisher, price, created_at } = props.book;
    return (
        <Card>
            <Card.Body>
                <Card.Title>책 제목 : <span style={titleStyle}>{title}</span></Card.Title>
                <Card.Text>{author}</Card.Text>
                <Card.Text>{publisher}</Card.Text>
                <Card.Text>{created_at}</Card.Text>
                <Card.Text>{price}</Card.Text>
                <Link to={`/bookDetail/${id}`}>상세보기</Link>
            </Card.Body>
        </Card>
    )
}

const style = StyleSheet.create({
    card: {
        width: '100%',
        height: '100%',
        backgroundColor: 'lightblue',
        borderRadius: 10,
        padding: 10,
        margin: 10,
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
    },
    title: {
        fontSize: 20,
        fontWeight: 'bold',
    },
    text: {
        fontSize: 16,
    },
    link: {
        fontSize: 16,
        color: 'blue',
        textDecoration: 'none',
    }
});

export default Book
